function process(event) {

  // Trigger or close SIGNL4 alert

  event.Title = "Alert from StreamPipes";
  event["X-S4-ExternalId"] = "Machine-A2";

  if (event.mass_flow >= 8) {
  	event["X-S4-Status"] = "new";
  }
  else {
    event["X-S4-Status"] = "resolved";
  }


  return event;
}
